const initialState = {
  good: 0,
  ok: 0,
  bad: 0
};

const counterReducer = (state = initialState, action) => {
  console.log(action);
  console.log(state.good);

  switch (action.type) {
    case 'GOOD':
      return state.good++;

    case 'OK':
      return state + 1;

    case 'BAD':
      return state + 1;

    case 'ZERO':
      return state;

    default:
      return state;
  }
};

export default counterReducer;
