const initialState = {
  good: 0,
  ok: 0,
  bad: 0
};

const counterReducer = (state = initialState, action) => {
  console.log(action);
  console.log(state);

  switch (action.type) {
    case 'GOOD':
      const plusGood = state.good + 1;
      const newGood = { ...state,
        good: plusGood
      };
      state = newGood;
      return state;

    case 'OK':
      return state + 1;

    case 'BAD':
      return state + 1;

    case 'ZERO':
      return state;

    default:
      return state;
  }
};

export default counterReducer;
